mapper.update:
update note set is_die=0 
  where note_id in 
  
      #{item}

update note set comment_num = comment_num + 1 

,baseComment_num=baseComment_num+1

where note_id=#{note_id}

mapper.select:
select a.*,b.nickname,b.head_image_url from note a, user b where a.user_id=#{user_id} and a.user_id=b.user_id and target_id=0
and note_id<#{start}
order by note_id desc limit 0,10
