mapper.select:
select a.user_id, a.telephone, a.fade_name, a.nickname, a.password, a.sex,a.head_image_url,a.register_time, a.summary, a.wechat_id, a.weibo_id, a.qq_id,a.concern_num, a.fans_num, a.fade_num, a.area, a.school_id, a.uuid, b.school_name,a.department_id, c.department_name, a.dynamicNum from user a, school b, department c
a.school_id=b.school_id and a.department_id=c.department_id and
a.wechat_id=#{open_id}
a.weibo_id=#{open_id}
a.qq_id=#{open_id}

select head_image_url from user  

    where telephone=#{telephone}


    where fade_name=#{fade_name}


    where wechat_id=#{wechat_id}

select user_id, addContributeNum,addFansNum,addCommentNum 
from addmessage 
where user_id=#{user_id}

select a.note_id,a.user_id, b.head_image_url, b.nickname, a.post_time, a.type, a.target_id,c.note_content 
 from note a, user b, note c
where a.target_id in (
    select note_id from note where user_id=#{user_id}
)

   and a.note_id<#{start}

and a.user_id=b.user_id
and a.post_time>=#{point}
and c.note_id=a.target_id
order by note_id desc
limit 0,20

select a.*, b.nickname as from_nickname, b.head_image_url as from_head,
c.note_content
from commentmessage a, user b, note c
where a.note_id=c.note_id
and a.to_id=#{user_id}
and a.from_id=b.user_id

   and a.message_id<#{start}

and a.comment_time>=#{point}
order by message_id desc
limit 0,20

select a.note_id,a.user_id, b.head_image_url, b.nickname, a.post_time, a.type, a.target_id, c.note_content
 from note a, user b, note c
where a.target_id in (
    select note_id from note where user_id=#{user_id}
)

   and a.note_id<#{start}

and a.user_id=b.user_id
and c.note_id=a.target_id
order by note_id desc
limit 0,20

select a.user_id, a.nickname, a.head_image_url, a.summary, b.relation_time, b.relation_id from user a, relation b where b.user_fans=a.user_id
and b.relation_id<#{start}
and b.user_star=#{user_id} order by relation_time desc limit 0,20

select a.*, b.nickname as from_nickname, b.head_image_url as from_head, c.note_content from commentmessage a, user b, note c where a.note_id=c.note_id and a.to_id=#{user_id} and a.from_id=b.user_id
and a.message_id<#{start}
order by message_id desc limit 0,20

select a.*, b.nickname, b.head_image_url
from myadd_second a, user b
where a.user_id=b.user_id
and a.user_id=#{user_id}
and a.note_id=#{note_id}

select nickname, user_id, head_image_url
  from user
 where user_id = #{user_id}

SELECT * FROM user where 
fans_num >= #{hotUserThreshold} 
and user_id not in
   
       #{item}
    	   
order by  RAND() limit #{num} ;
